package javaBasics;

public class Sum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1, n2, s;
		n1 = 50;
		n2 = 6;
		s = n1 + n2;
		System.out.println("Sum is" + s--);
		System.out.println("Sum is" + s--);
		int d=n1/n2;
		System.out.println(d);
		int e=n1%n2;
		System.out.println(e);
		boolean b=true;
		System.out.println(b);
	}
}
