package javaBasics;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1, n2, temp;
		n1 = 20;
		n2 = 40;
		System.out.println("the values of n1 and n2 are" + n1 + " "+ n2);
		temp = n1;
		n1 = n2;
		n2 = temp;
		System.out.println("the values of n1 and n2 are" + n1 +" "+ n2);

	}

}
